package com.nextlabs.hb.helper;import java.text.MessageFormat;import java.util.ArrayList;import java.util.Iterator;public class InsertQueryHelper {	String tableName;	ArrayList<String> fieldList;	String query;	public InsertQueryHelper() {		tableName = null;		fieldList = null;		query = null;	}	public String getTableName() {		return tableName;	}	public void setTableName(String tableName) {		this.tableName = tableName;	}	public ArrayList<String> getFieldList() {		return fieldList;	}	public void setFieldList(ArrayList<String> fieldList) {		this.fieldList = fieldList;	}	public String getQuery() {		return query;	}	public void setQuery(String query) {		this.query = query;	}	public void initalize(String tableName) {		this.tableName = tableName;		query = "INSERT INTO {0} ({1}) VALUES({2})";		fieldList = new ArrayList<String>();	}	public void prepareQuery() {		StringBuilder fields = new StringBuilder();		StringBuilder qms = new StringBuilder();		Iterator<String> iter = fieldList.iterator();		while (iter.hasNext()) {			String field = iter.next();			fields.append(field);			qms.append("?");			if (iter.hasNext()) {				fields.append(",");				qms.append(",");			}		}		query = MessageFormat.format(query, tableName, fields.toString(),				qms.toString());	}	public static void main(String args[]) {		InsertQueryHelper iqh = new InsertQueryHelper();		iqh.initalize("AuthorityResources");		iqh.getFieldList().add("authorityid");		iqh.getFieldList().add("resource");		iqh.prepareQuery();		System.out.println(iqh.query);	}}